/**
 * Shows the HTML element using Bootstrap.
 * 
 * @parameter object element	HTML element (jQuery).
 * 
 */
 function show(element) {
	 element.removeClass('hidden');
 }
 
/**
 * Hides the HTML element using Bootstrap.
 * 
 * @parameter object element	HTML element (jQuery).
 * 
 */
 function hide(element) {
	 element.addClass('hidden');
 }
 
 
